import { Delete, DocumentAdd, Edit } from "@element-plus/icons-vue";
import { ref } from "vue";
import axios from "@/util/axios";
import AddDialog from "@/views/question/components/add";
import EditDialog from "@/views/question/components/edit";
import EditItemsDialog from "@/views/question/components/editItems";
import { ElMessage, ElMessageBox } from "element-plus";

// 数据定义

export default {
  __name: 'index',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const tableData = ref([]); // 表格数据
    const dialogVisible = ref(false); // 控制AddDialog是否可见
    const dialogVisible2 = ref(false); // 控制EditDialog是否可见
    const dialogVisible3 = ref(false); // 控制EditItemsDialog（编辑题目选项界面）是否可见
    const questionId = ref(-1); // 题目ID，用于EditDialog、EditItemsDialog的数据初始化

    /**
     * 获取表格数据
     * @returns {Promise<void>}
     */
    const initList = async () => {
      // 请求数据
      const res = await axios.get('/api/survey-question/all');
      // 赋值给tableData
      tableData.value = res.data.data.surveyQuestions;
    };
    initList();

    /**
     * 点击添加时调用，使AddDialog可见（即弹出新增数据页面）
     */
    const handleAddDialog = () => {
      // 使AddDialog可见
      dialogVisible.value = true;
    };

    /**
     * 点击修改时调用，使EditDialog可见（即弹出修改数据页面）
     * @param id
     */
    const handleEditDialog = id => {
      // 将触发时所在行的id赋值给questionId
      questionId.value = id;
      // 使EditDialog可见
      dialogVisible2.value = true;
    };

    /**
     * 点击编辑选项时调用，使EditItemsDialog可见（即弹出编辑选项页面）
     * @param id
     */
    const handleEditItemsDialog = id => {
      // 将触发时所在行的id赋值给questionId
      questionId.value = id;
      // 使EditItemsDialog可见
      dialogVisible3.value = true;
    };

    /**
     * 将类型ID翻译成对应的类型名称
     */
    const cateFormatter = row => {
      switch (row.surveyCateId) {
        case 1:
          return "单选题";
        case 2:
          return "多选题";
        case 3:
          return "判断题";
        default:
          return "简答题";
      }
    };

    /**
     * 点击删除时调用
     * @param id
     */
    const handleDelete = id => {
      ElMessageBox.confirm('确定要删除吗？', '系统提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        // 向后端发送删除请求
        let res = await axios.get('/api/survey-question/delete/' + id);
        if (res.data.success === true) {
          ElMessage({
            type: 'success',
            message: '删除成功'
          });
          // 请求成功后刷新表格数据
          initList();
        } else {
          ElMessage({
            type: 'error',
            message: res.data.msg
          });
        }
      }).catch(() => {});
    };
    const __returned__ = {
      tableData,
      dialogVisible,
      dialogVisible2,
      dialogVisible3,
      questionId,
      initList,
      handleAddDialog,
      handleEditDialog,
      handleEditItemsDialog,
      cateFormatter,
      handleDelete,
      get Delete() {
        return Delete;
      },
      get DocumentAdd() {
        return DocumentAdd;
      },
      get Edit() {
        return Edit;
      },
      ref,
      get axios() {
        return axios;
      },
      get AddDialog() {
        return AddDialog;
      },
      get EditDialog() {
        return EditDialog;
      },
      get EditItemsDialog() {
        return EditItemsDialog;
      },
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
