import { ref, watch } from "vue";
import axios from "@/util/axios";
import { ElMessage, ElMessageBox } from "element-plus";
import { Delete, Edit } from '@element-plus/icons-vue';
import EditDialog from "@/views/question/components/editItemName";
export default {
  __name: 'editItems',
  props: {
    dialogVisible: {
      type: Boolean,
      default: false,
      required: true
    },
    id: {
      type: Number,
      default: -1,
      required: true
    }
  },
  emits: ['update:modelValue', 'initList'],
  setup(__props, {
    expose: __expose,
    emit: emits
  }) {
    __expose();
    const props = __props;
    const formRef = ref(null);
    const form = ref({
      id: null,
      item: "",
      userId: 0
    });
    const rules = ref({
      item: [{
        required: true,
        message: '请输入选项内容'
      }]
    });
    const tableData = ref([]);
    const initItemList = async () => {
      const res = await axios.get('/api/survey-item/all');
      tableData.value = res.data.data.surveyItems;
    };
    watch(() => props.dialogVisible, () => {
      if (props.dialogVisible === true) {
        initItemList();
        initData(props.id);
      }
    });
    const handleAddItem = async () => {
      formRef.value.validate(async valid => {
        if (valid) {
          let res = await axios.post('/api/survey-item/add', form.value);
          if (res.data.success === true) {
            ElMessage.success("执行成功！");
            formRef.value.resetFields();
            initItemList();
          } else {
            ElMessage.error(res.data.msg);
          }
        }
      });
    };
    const addItemIds = ref("0");
    const handleSelectionChange = val => {
      let idList = val.map(item => {
        return item.id;
      });
      if (idList.length === 0) {
        addItemIds.value = "0";
        return;
      }
      let itemIds = "";
      idList.forEach((item, index) => {
        itemIds += item;
        if (index !== idList.length - 1) {
          itemIds += ",";
        }
      });
      console.log(itemIds);
      addItemIds.value = itemIds;
    };
    const question = ref({});
    const initData = async id => {
      const res = await axios.get('/api/survey-question/one/' + id);
      question.value = res.data.data.surveyQuestion;
    };
    const handleChangeItems = async () => {
      question.value.surveyItemIds = addItemIds.value;
      let res = await axios.post('/api/survey-question/edit', question.value);
      if (res.data.success === true) {
        ElMessage.success("执行成功！");
        emits('initList');
        handleClose();
      } else {
        ElMessage.error(res.data.msg);
      }
    };
    const editItemId = ref(0);
    const editItemDialogVisible = ref(false);
    const handleEdit = id => {
      // editItemId.value = id;
      // editItemDialogVisible.value = true;
      ElMessageBox.prompt('修改选项内容', '内容', {
        confirmButtonText: '确定',
        cancelButtonText: '取消'
      }).then(async ({
        value
      }) => {
        let newItem = {
          id: id,
          item: value,
          userId: 3
        };
        let res = await axios.post('/api/survey-item/edit', newItem);
        if (res.data.success === true) {
          ElMessage.success("执行成功！");
          initItemList();
        } else {
          ElMessage.error(res.data.msg);
        }
      }).catch(() => {});
    };
    const handleDelete = id => {
      ElMessageBox.confirm('您确定要删除这条记录吗？', '系统提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        let res = await axios.get('/api/survey-item/delete/' + id);
        if (res.data.success === true) {
          ElMessage({
            type: 'success',
            message: '删除成功'
          });
          initItemList();
        } else {
          ElMessage({
            type: 'error',
            message: res.data.msg
          });
        }
      }).catch(() => {});
    };
    const handleClose = () => {
      formRef.value.resetFields();
      form.value = {
        id: null,
        item: "",
        userId: 0
      };
      emits('update:modelValue', false);
    };
    const __returned__ = {
      props,
      formRef,
      form,
      rules,
      tableData,
      initItemList,
      emits,
      handleAddItem,
      addItemIds,
      handleSelectionChange,
      question,
      initData,
      handleChangeItems,
      editItemId,
      editItemDialogVisible,
      handleEdit,
      handleDelete,
      handleClose,
      ref,
      watch,
      get axios() {
        return axios;
      },
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      },
      get Delete() {
        return Delete;
      },
      get Edit() {
        return Edit;
      },
      get EditDialog() {
        return EditDialog;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
