import { ref, watch } from "vue";
import axios from "@/util/axios";
import { ElMessage } from "element-plus";
export default {
  __name: 'editQuestion',
  props: {
    dialogVisible: {
      type: Boolean,
      default: false,
      required: true
    },
    id: {
      type: Number,
      default: -1,
      required: true
    }
  },
  emits: ['update:modelValue'],
  setup(__props, {
    expose: __expose,
    emit: emits
  }) {
    __expose();
    const props = __props;
    const tableData = ref([]);
    const initQuestionList = async () => {
      const res = await axios.get('/api/survey-question/all');
      tableData.value = res.data.data.surveyQuestions;
    };
    const cateFormatter = row => {
      switch (row.surveyCateId) {
        case 1:
          return "选择题";
        case 2:
          return "判断题";
        case 3:
          return "简答题";
        default:
          return row.surveyCateId;
      }
    };
    watch(() => props.dialogVisible, () => {
      if (props.dialogVisible === true) {
        initQuestionList();
        initData(props.id);
      }
    });
    const questionIds = ref("0");
    const handleSelectionChange = val => {
      let idList = val.map(item => {
        return item.id;
      });
      if (idList.length === 0) {
        questionIds.value = "0";
        return;
      }
      let itemIds = "";
      idList.forEach((item, index) => {
        itemIds += item;
        if (index !== idList.length - 1) {
          itemIds += ",";
        }
      });
      console.log(itemIds);
      questionIds.value = itemIds;
    };
    const assembly = ref({});
    const initData = async id => {
      const res = await axios.get('/api/survey-assembly/one/' + id);
      assembly.value = res.data.data.assembly;
    };
    const handleConfirm = async () => {
      assembly.value.surveyQuestonIds = questionIds.value;
      let res = await axios.post('/api/survey-assembly/edit', assembly.value);
      if (res.data.success === true) {
        ElMessage.success("执行成功！");
        handleClose();
      } else {
        ElMessage.error(res.data.msg);
      }
    };
    const handleClose = () => {
      emits('update:modelValue', false);
    };
    const __returned__ = {
      props,
      tableData,
      initQuestionList,
      cateFormatter,
      questionIds,
      handleSelectionChange,
      assembly,
      initData,
      handleConfirm,
      emits,
      handleClose,
      ref,
      watch,
      get axios() {
        return axios;
      },
      get ElMessage() {
        return ElMessage;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
